#include <iostream>
#include <cstring>
#include <algorithm>
#include <deque>
#include <map>
#include <set>
#include <unordered_map>
#include <unordered_set>
#include <cctype>
#include <numeric>
#include <functional>

using namespace std;
#define int long long

string input="";
map<char,int> m;
const int length=4;//=14 for part 2
void ins(char c){
  if(m.find(c)==m.end()){
    m.insert(make_pair(c,0));
  }
  m[c]++;
}

void rem(char c){
  m[c]--;
  if(m[c]==0){
    m.erase(c);
  }
}


signed main(){
  ios_base::sync_with_stdio(0);
  freopen("tmp.txt","r",stdin);
  for(int i=0;i<=length-1;++i){
    ins(input[i]);
  }
  if(m.size()==length){
    cout<<length;
    return 0;
  }
  for(int i=length;i<input.size();++i){
    rem(input[i-length]);
    ins(input[i]);
    if(m.size()==length){
        cout<<i+1<<endl;
        break;
    }
  }
  return 0;
}

